import osimport timedef print_arrow_Right(n):	for i in range (6):		print("\n")	for i in range(n):		print("                           ",end="")		for j in range(i+1):			print("*",end="")		print()	print("                    ",end="")	print("***************")	for i in range(n,0,-1):		print("                           ",end="")		for j in range(i):			print("*",end="")		print()	#print_arrow_Right(5)def print_arrow_left(n):	for i in range (6):		print("\n")	for i in range(n):		print("        ",end="")		for j in range(n):			if j>=n-i-1:				print("*",end="")			else:				print(" ",end="")		print()	print("     ",end="")	print("***************")	for i in range(n,0,-1):		print("        ",end="")		for j in range(n):			if j>=n-i:				print("",end="")				print("*",end="")			else:				print(" ",end="")		print()			#print_arrow_left(5)def print_arrow_lower(n):	for i in range (12):		print("\n")	for i in range(n):		print("               ",end="")		for j in range(n):			if j==n-3:				print("*",end="")			else:				print(" ",end="")		print()	for i in range(n,0,-2):		print("             ",end="")		for j in range(n-1):			if j>=n-i :				print(" ",end="")				print("*",end="")			else:				print(" ",end="")		print()			#print_arrow_lower(8)def print_arrow_uper(n):	for i in range (2):		print("\n")	for i in range(n+1,0,-2):		print("            ",end="")		for j in range(n):			if j>=i :				print(" ",end="")				print("*",end="")			else:				print(" ",end="")		print()		for i in range(n):		print("               ",end="")		for j in range(n):			if j==n-3:				print("*",end="")			else:				print(" ",end="")		print()#print_arrow_uper(8)print("your options :")print("1-Rotate Clock Wise")print("2-Rotate Anti Clock Wise")x=int(input("Enter your Direction : "))if x==1:    #to rotate clock wise	while True:		print_arrow_Right(5)		# Wait for 0.1 seconds		time.sleep(.1)		# Clear the screen		os.system("cls" if os.name == "nt" else "clear")		print_arrow_lower(8)		time.sleep(.1)		# Clear the screen		os.system("cls" if os.name == "nt" else "clear")		print_arrow_left(5)		time.sleep(.1)		# Clear the screen		os.system("cls" if os.name == "nt" else "clear")		print_arrow_uper(8)		time.sleep(.1)		# Clear the screen		os.system("cls" if os.name == "nt" else "clear")elif x==2:    #to rotate anti clock wise	while True:		print_arrow_Right(5)		# Wait for .1 seconds		time.sleep(.1)		# Clear the screen		os.system("cls" if os.name == "nt" else "clear")		print_arrow_uper(8)		time.sleep(.1)		# Clear the screen		os.system("cls" if os.name == "nt" else "clear")		print_arrow_left(5)		time.sleep(.1)		# Clear the screen		os.system("cls" if os.name == "nt" else "clear")		print_arrow_lower(8)		time.sleep(.1)		# Clear the screen		os.system("cls" if os.name == "nt" else "clear")		