def calculator():	print("your options\n1-scientific calculator\n2-programing calculator")	case=int(input("Enter your case : "))	if case == 1:		num1=float(input("Enter the first number:"))		operation=input("Enter an operation (+, -, *, /) :")		num2=float(input("Enter the first number:"))			if   operation=='+':			result= num1 + num2		elif operation=='-':			result= num1 - num2		elif operation=='*':			result= num1 * num2		elif operation=='/':			result= num1 / num2		else:			print("Invalid operation")		print("Result: ",result)		return			elif case == 2:		num3=int(input("Enter the first number:"))		operation=input("Enter an operation (&, |, <<, >>) :")		num4=int(input("Enter the first number:"))		if   operation=='&':			result= num3 & num4		elif operation=='|':			result= num3 | num4		elif operation=='<<':			result= num3 << num4		elif operation=='>>':			result= num3 >> num4		else:			print("Invalid operation")		print("Result: ",result)		return	else :		print("Invalid")calculator()